import React, { useEffect, useState, useRef } from 'react';
import './App.css';
import {AnimatePresence, motion} from "framer-motion"
const WebSocketComponent = ({ ws }) => {
  const [blocks, setBlocks] = useState([]);

  useEffect(() => {
    ws.current.onmessage = (event) => {
      const message = event.data;
      const currentTime = new Date().toLocaleTimeString(); // 获取当前系统时间
      const newBlock = { message, time: currentTime }; // 创建包含消息和时间的对象

      setBlocks(prevBlocks => [...prevBlocks, newBlock]); // 添加新的消息和时间到展示区域
    };

    return () => {
      if (ws.current && ws.current.readyState === WebSocket.OPEN) {
        ws.current.close();
      }
    };
  }, [ws]);

  return (
      <div className="debugWindow">
        {blocks.map((block, index) => (
            <div key={index} className="messageBlock">
              <p>{'<' + block.time + '>    ' + block.message}</p>
            </div>
        ))}
        <button className="debugButton">click</button>
      </div>
  );
};

function Analysis() {
  return (
      <div className={"analysisWindow"}>
        <p>This is an analysis window</p>
      </div>
  )
}

const BouncingBallBackground = () => {
    const [ballPosition, setBallPosition] = useState({
        top: Math.random() * 90 + 'vh',
        left: Math.random() * 90 + 'vw',
    });

    const getRandomPosition = () => {
        return {
            top: Math.random() * 90 + 'vh',
            left: Math.random() * 90 + 'vw',
        };
    };

    const handleAnimationComplete = () => {
        setBallPosition(getRandomPosition());
    };

    useEffect(() => {
        const interval = setInterval(() => {
            setBallPosition(prevPosition => {
                let newTop = prevPosition.top;
                let newLeft = prevPosition.left;

                if (newTop > '40vh' && newTop < '60vh' && newLeft > '40vw' && newLeft < '60vw') {
                    // 如果小球即将进入中间区域，调整移动方向
                    newTop = Math.random() > 0.5 ? '10vh' : '80vh';
                    newLeft = Math.random() > 0.5 ? '10vw' : '80vw';
                }

                return { top: newTop, left: newLeft };
            });
        }, 20); // 每2秒检查一次位置

        return () => clearInterval(interval);
    }, []);

    return (
            <motion.div
                style={{
                    width: 20,
                    height: 20,
                    borderRadius: '50%',
                    backgroundColor: 'red',
                    position: 'absolute',
                    top: ballPosition.top,
                    left: ballPosition.left,
                }}
                animate={{
                    top: ballPosition.top,
                    left: ballPosition.left,
                    opacity: [1, 0.5, 1], // 设置透明度渐变
                }}
                transition={{ duration: 2, ease: 'easeInOut' }}
                onAnimationComplete={handleAnimationComplete}
            />
    );
};





function App() {
  const ws = useRef(new WebSocket('ws://127.0.0.1:8081'));

  return (
      <div className="mainContainer">
          {Array.from({ length: 10 }, (_, i) => (
          <BouncingBallBackground key={i} />
          ))}
        <div className="title">
          <p className="titleContent">Interface</p>
        </div>
        <div className="contentContainer">
          <div className="componentContainer">
            <WebSocketComponent ws={ws}/>
          </div>
          <div className="componentContainer">
            <Analysis />
          </div>
        </div>
      </div>
  );
}

export default App;
