import React, {useEffect, useState, useRef} from 'react';
import './Interface.css';
import {motion} from "framer-motion";
import {LoadingRectangle, MeteorAnimation, AnimationSwitchComponent, BackgroundSwitchComponent} from './components.js'

const hostIP = "10.12.171.80"

//WebSocketComponent:接收node程序转发的消息，展示
const WebSocketComponent = ({ ws }) => {
    const [blocks, setBlocks] = useState([]);
    const debugWindowRef = useRef(null);
    const analysisMessagesRef = useRef(["test"]);

    useEffect(() => {
        if (debugWindowRef.current) {
            debugWindowRef.current.scrollTop = debugWindowRef.current.scrollHeight;
        }
    }, [blocks]);

    useEffect(() => {
        ws.current.onmessage = (event) => {
            const message = event.data;
            if (message.includes("[tips]")) {
                analysisMessagesRef.current = [...analysisMessagesRef.current, message];
            }
            const currentTime = new Date().toLocaleTimeString();
            const newBlock = { message, time: currentTime };
            setBlocks(prevBlocks => [...prevBlocks, newBlock]);
        };

        return () => {
            if (ws.current && ws.current.readyState === WebSocket.OPEN) {
                ws.current.close();
            }
        };
    }, [ws]);

    return (
        <div className={"mainInterface"}>
            <div ref={debugWindowRef} className="debugWindow">
                {blocks.map((block, index) => (
                    <motion.div
                        key={index}
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ duration: 0.5, delay: 0.8 }}
                        className="messageBlock"
                        whileHover={{ scale: 1.03, backgroundColor: "#f8f8f8", fontSize: "25px", transition: {delay: 0.1 }}}
                    >
                        <p>{'<' + block.time + '>    ' + block.message}</p>
                    </motion.div>
                ))}
            </div>
            <AnalysisWindow analysisMessages={analysisMessagesRef}/>
        </div>
    );
};

//AnalysisWindow:结果分析窗口
function AnalysisWindow({analysisMessages}) {
    return (
        <div className="analysisWindow">
            {analysisMessages.current.map((message, index) => (
                <motion.div
                    key={index}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5, delay: 0.8 }}
                    className="messageBlock"
                    whileHover={{ scale: 1.03, backgroundColor: "#f8f8f8", fontSize: "25px", transition: {delay: 0.1 }}}
                >
                    <p>{message}</p>
                </motion.div>
            ))}
        </div>
    );
}

function AnalysisButton({text}) {
    return (
        <motion.button
            className="debugButton"
            style={{
                width: "calc(5vw + 10px)",
                height: "5vh",
                borderRadius: '15%',
                backgroundImage: "linear-gradient(120deg,#e0c3fc 0%, #8ec5fc 100%)",
            }}
            initial={{x: 0, y: "5vh"}}
            whileHover={{opacity: 0.8}}
            transition={{ duration: 0.5, ease: 'easeInOut'}}
        >{text}</motion.button>
    );
}

const BouncingBallBackground = ({props}) => {
    const {startTop, scaleTop, startLeft, scaleLeft} = props;

    const [ballPosition, setBallPosition] = useState({
        top: '50vh',
        left: '50vw',
    });

    const getRandomPosition = () => {
        return {
            top: startTop + Math.random() * scaleTop + 'vh',
            left: startLeft + Math.random() * scaleLeft + 'vw',
        };
    };

    const handleAnimationComplete = () => {
        setBallPosition(getRandomPosition());
    };

    return (
        <motion.div
            style={{
                width: 5,
                height: 5,
                borderRadius: '50%',
                backgroundImage: "linear-gradient(0deg,#fbc2eb 0%, #a6c1ee 100%)",
                position: 'absolute',
                top: ballPosition.top,
                left: ballPosition.left,
            }}
            animate={{ top: getRandomPosition().top, left: getRandomPosition().left, opacity: [1, 0, 1]}}
            transition={{ duration: 5, ease: 'easeInOut'}}
            onAnimationComplete={handleAnimationComplete}
        />
    );
};

function AnimatedBalls({number}) {
    return (
        <div>
            {Array.from({ length: number }, (_, i) => (
                <BouncingBallBackground key={i} props={{startTop: 0, scaleTop: 10, startLeft: 0, scaleLeft: 90}}/>
            ))}
            {Array.from({ length: number }, (_, i) => (
                <BouncingBallBackground key={i + 5} props={{startTop: 88, scaleTop: 10, startLeft: 0, scaleLeft: 90}}/>
            ))}
            {Array.from({ length: number }, (_, i) => (
                <BouncingBallBackground key={i + 10} props={{startTop: 0, scaleTop: 90, startLeft: 0, scaleLeft: 10}}/>
            ))}
            {Array.from({ length: number }, (_, i) => (
                <BouncingBallBackground key={i + 15} props={{startTop: 0, scaleTop: 90, startLeft: 88, scaleLeft: 10}}/>
            ))}
        </div>
    );
}

//Interface:包含各种组件的主页面
function Interface() {
  const ws = useRef(new WebSocket(`ws://${hostIP}:8081`));
  const [backgroundSwitch, setBackgroundSwitch] = useState(false);
  const [animationSwitch, setAnimationSwitch] = useState(false);
  const handleBackgroundSwitch = () => {
      setBackgroundSwitch(!backgroundSwitch);
  }
  const handleAnimationSwitch = () => {
      setAnimationSwitch(!animationSwitch);
  }
  return (
      <motion.div className="mainContainer" style={{backgroundImage: backgroundSwitch? "linear-gradient(132deg, rgb(65, 80, 95) 0.00%, rgb(36, 37, 38) 100.00%)" : "linear-gradient(132deg, rgb(59, 55, 106) 0.00%, rgb(0, 143, 186) 50.00%, rgb(255, 149, 213) 100.00%)"}}>
          <LoadingRectangle/>
          <MeteorAnimation animationSwitch={animationSwitch} number={5}/>
          {/*<AnimatedBalls number={1}/>*/}
          <BackgroundSwitchComponent handleBackgroundSwitch={handleBackgroundSwitch} backgroundSwitch={backgroundSwitch}/>
          <AnimationSwitchComponent handleAnimationSwitch={handleAnimationSwitch} animationSwitch={animationSwitch}/>
        <div className="title">
          <motion.p
              style={{
                  color: "transparent",
                  backgroundImage: "linear-gradient(0deg,#fbc2eb 0%, #a6c1ee 100%)",
                  WebkitBackgroundClip: "text",
                  backgroundClip: "text"
                }}
              className="titleContent"
              initial={{opacity: 0.5, y: -150}}
              animate={{opacity: 1, y: 0, transition: {duration: 3}}}
              whileTap={{scale: 0.8}}
          >Interface
          </motion.p>
        </div>
          <WebSocketComponent ws={ws}/>
          <AnalysisButton text={"分析"}/>
      </motion.div>
  );
}

export default Interface;
