import {motion} from "framer-motion";
import React from "react";

export const LoadingRectangle = () => {
    return (
        <motion.div
            style={{
                backgroundImage: "linear-gradient(0deg,#fbc2eb 0%, #a6c1ee 100%)",
                width: "30px",
                height: "30px",
                borderRadius: '10%',
                position: "absolute",
                x: "calc(90px + 1.5vw)",
                y: "9.4vh",
                cursor: "pointer"
            }}
            initial={{ rotate: 0}}
            animate={{ rotate: 360 }}
            transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
            onClick={()=>window.location.reload()}
        />
    );
};

export const Meteor = ({delay}) => {
    return (
        <motion.div
            className={"meteor"}
            initial={{ opacity: 0, rotate: 45, x: '48vw', y: 0 }}
            animate={{ opacity: [1, 0, 1], x: '-48vw', y: '96vh' }}
            transition={{ duration: 10, ease: 'easeInOut', delay: delay, repeat: Infinity }}
        >
        </motion.div>
    );
};

export const MeteorAnimation = ({ animationSwitch ,number }) => {
    const stars = Array.from({ length: number }, (v, i) => i);
    return (
        <div className={"night"} style={{display: animationSwitch? 'none' : 'initial'}}>
            {stars.map((index) => (
                <Meteor key={index} delay={index}/>
            ))}
        </div>
    );
};

export const AnimationSwitchComponent = ({handleAnimationSwitch, animationSwitch}) => {
    return (
        <motion.div style={{
            width: "100px",
            height: "40px",
            top: "15vh",
            left: "5vw",
            padding: "5px",
            backgroundColor: '#FFFFFF88',
            borderRadius: '60px',
            position: "absolute",
            display: "flex",
            justifyContent: "center",
            flexDirection: "column",
            border: "1px solid black",
        }}>
            <motion.div
                style={{
                    width: "30px",
                    height: "30px",
                    borderRadius: '50%',
                    backgroundImage: "linear-gradient(0deg,#fbc2eb 0%, #a6c1ee 100%)",
                    cursor: "pointer"
                }}
                initial={{x: 0, y: 0}}
                animate={animationSwitch? {x: "70px", y: 0} : {x: 0, y: 0}}
                onClick={handleAnimationSwitch}
            />
            <motion.p className={"functionText"} style={{position: "absolute", left: "-50px" }}>动画</motion.p>
        </motion.div>
    );
};

export const BackgroundSwitchComponent = ({handleBackgroundSwitch, backgroundSwitch}) => {
    return (
        <motion.div style={{
            width: "100px",
            height: "40px",
            top: "5vh",
            left: "5vw",
            padding: "5px",
            backgroundColor: '#FFFFFF88',
            borderRadius: '60px',
            position: "absolute",
            display: "flex",
            justifyContent: "center",
            flexDirection: "column",
            border: "1px solid black",
        }}>
            <motion.div
                style={{
                    width: "30px",
                    height: "30px",
                    borderRadius: '50%',
                    backgroundImage: "linear-gradient(0deg,#fbc2eb 0%, #a6c1ee 100%)",
                    cursor: "pointer"
                }}
                initial={{x: 0, y: 0}}
                animate={backgroundSwitch? {x: "70px", y: 0} : {x: 0, y: 0}}
                onClick={handleBackgroundSwitch}
            />
            <motion.p className={"functionText"} style={{position: "absolute", left: "-50px" }}>背景</motion.p>
        </motion.div>
    );
};
